import { useParams } from "react-router-dom";
import {useQuery} from "react-query"
import {useState,useEffect} from "react"
import axios from "axios";
import { atom,useRecoilState } from "recoil";
import {AddCart} from "./AddCart"
import {cartAtom, useCart} from "../atoms/cartAtom"
import '../App.css';

//this logic should be present in a separate file under Store folder (teja)


export const Products=()=>{


    const [count,setCount]=useState(0)

    const [cart,setCart]=useCart()
    //this logic can be moved to the Store file under custom hook named "useCart"
    const {id}=useParams()
    const [cartvalue,setCartValue]=useState(0);


    //console.log(id)
    const { isLoading, isError, data, error }= useQuery(['product', id],async ()=>{
        return  await axios.get(`https://obscure-refuge-62167.herokuapp.com/products/${id}`);
      })


    const [imgsrc,setImgsrc]=useState('')
    useEffect(()=>
    {
      if (data) 
      {
        setImgsrc(data.data.image);
      }
    }, [data]);

    const [currentQty,setCurrentQty]=useState(0)
    useEffect(()=>
    {
      if (data) 
      {
        setCurrentQty(data.data.quantity);
      }
    }, [data]);


     // const handleChange =(img)=>
     // {
     //   setImgsrc(img);
     // };
     // const handleDisable =(count)=>
     // {
     //  return count===0?"true":"false" 
     // };

      if (isLoading) {
        return <span>Loading...</span>
      }

      if (isError) {
        console.log(error)
        return <span>Error: {error.message}</span>
      }
      //let t=""
      console.log(data.data.quantity)
      console.log(count)



      const {id:i,name,price,quantity,variants,image} = data.data;
      //setText(quantity>10?"available":quantity>=1?"sellingfast":"unavailable")
      // console.log(variants.image)
      //disabled={dis}
      //setDis(
       // (count ===0) ? "true":"false" 
      //)
      console.log(count)
     // const addToCart2=()=>{
     //   setCart({
     //     ...cart,
     //    
     // {...cart,cart[id]:cart[id]+1}// cart[id]:cart[id]+1
     //   });
     //   setCurrentQty({currentQty-1})
     // }
      const addToCart=()=>{           //array->object
        setCart(()=>[
          {
            id:i,
            image:image,
            name:name,
            quantity:currentQty,
            price:price * currentQty,
          }
        ]);
        console.log(quantity)

      };

      const remFromCart=()=>{
        setCart(()=>[

        ]);
      }
      const text=""
      const qty=quantity

      const getcurrqtytxt=()=>{
        if(qty==0)
        {return "unavailable"}
        else if(qty>0)
        {return "sellingFast"}
        else
        {return "available"}
      }


      const loadCart=()=>{
        return <AddCart id={i}/>
      }
      console.log(cart)
      console.log(cart.length)
     // console.log(count)
     // let cname=cart.length>0?"dshow":"show"
     // cname=cart.length==0?"show":"dshow"
      //const handleCart=()=>{
      //  return <div className="cartDisplay">Hello</div>
      //  
      //}

    return (

      data.data?

        <div className="item">

            <img className="link" width={400} height={400} src={imgsrc} alt="product-1" />
            <div  className="description">
                <h3>{data.data.name}</h3>
                <p>Description: {data.data.description}</p>
                <p>Price:<strong>${data.data.price}</strong></p>

                <p>Variants: </p>
                {
                  variants.map((variant)=>{
                    return <button id="btnd" key={variant.color} style={{backgroundColor:variant.color,width:"40px",height:"40px"}}
                    onClick={() => setImgsrc(variant.image)}></button>
                  })
                }

                <p>Quantity:{getcurrqtytxt}{currentQty}</p> 
                <p>{currentQty}</p>
                {
                  count>0?
                  <div><button id="btn" disabled={count===0?true:false} onClick={()=>{setCount(count-1);

                    count>0?addToCart():remFromCart(); }}>-</button>
                    {cart[data['id']]}
                    <button id="btn" disabled={(count>=data.data.quantity)?true:false}  onClick={()=>{setCount(count+1);
                    addToCart()}} >+</button>
                    </div>
                    :
                  <button  id="btn" onClick={()=>{setCount(1);addToCart();setCartValue(cartvalue+1)}} style={{fontSize:"15px"}}>Add to Cart</button>
                }




            </div>   

        </div>:"undefined"


    );



//    import './product-page-styling.css'
//import { useQuery } from '@tanstack/react-query'
//import axios from 'axios'
//import {useParams} from 'react-router-dom'
//import {useState,useEffect} from 'react'
//import {useRecoilState} from 'recoil';
//import {cartAtom} from '../atoms/cartAtom'

function Product(){

    const [currentCart,setCart]=useRecoilState(cartAtom);
    const params=useParams();
    let productId=params['id'];
    if (params['id']===':id')
        productId=1;
    const {data,status,isFetching}=useQuery([`showProduct-${productId}`], async () => {const { data } = await axios.get(`https://obscure-refuge-62167.herokuapp.com/products/${productId}`);
                                                                    return data;}
                                            );

    const [currentImage,setCurrentImage]=useState('')
    useEffect(()=>{
        if (data){
            setCurrentImage(data?.image)
        }
    },[data])

    const [currentQuantity,setQuantity]=useState(0)
    useEffect(()=>{
        if (data){
            setQuantity(data?.quantity-(currentCart[data['id']]? currentCart[data['id']]:0))
        }
    },[data,currentCart])

    if (status==='error')
        return <h1>Server Down</h1>
    if (status==='loading' || isFetching)
        return <h1>Loading...</h1>

    function addToCart(){
        setCart({...currentCart,[data.id]:1})
        setQuantity(currentQuantity-1);
    }

    function getQuantityText(){
        if (currentQuantity>10)
            return 'Available'
        else if (currentQuantity===0)
            return 'Unavailable'
        else if (currentQuantity<=10)
            return 'Selling Fast ⚡⚡⚡'
        else    
            return 'not enough data';
    }

    function triggerChangeInQuantity(sign){
        if (sign==='+'){
            setCart({...currentCart,[data.id]:currentCart[data.id]+1})
            setQuantity(currentQuantity-1);
        }
        else { 
            if (currentCart[data['id']]===1){
                let a={...currentCart}
                delete a[data.id]
                setCart(a);
                setQuantity(data['quantity']);
            }
            else{
                setCart({...currentCart,[data.id]:currentCart[data.id]-1})
                setQuantity(currentQuantity+1);
            }

        }
    }

    return (
        <div className='product-page-container'>
            <div className='product-page-image'>
                <img className='card-image' style={{width:'100%',height:'100%'}} src={currentImage} alt="bottles"/>
            </div>
            <div className='product-page-product-description'>
                <h2>{data['name']}</h2>
                <p>{data['description']}</p>
                <p className='product-page-price'>Price : <span>{`$ ${data['price']}`}</span></p>
                {/* <p>Left Over: {currentQuantity}</p> */}
                <p>{getQuantityText()}</p>
                {data?.variants.map((variant,index)=>{
                    return <button className='variant-buttons' key={variant.color} style={{backgroundColor: variant.color}} onClick={()=>{ setCurrentImage(variant?.image) }}></button>
                })}
                <br/>

                {(data['id'] in currentCart)?
                    (<div className='counter'>                
                        <button className='quantity-buttons' disabled={currentQuantity===0?true:false} onClick={ () =>  triggerChangeInQuantity('+')  }>+</button>
                        <p>{currentCart[data['id']]}</p>
                        <button className='quantity-buttons' disabled={currentQuantity===data['quantity']?true:false} onClick={ () =>  triggerChangeInQuantity('-')  }>-</button>
                    </div>):
                    <button className='add-to-cart-button' onClick={addToCart}>Add to Cart</button>
                }
            </div>
        </div>
    )

}
}
