import { useParams } from "react-router-dom";
import {useQuery} from "react-query"
import {useState} from "react"
import axios from "axios";
import '../App.css';

export const Products=()=>{

    const [count,setCount]=useState(0)
    const [text,setText]=useState("")
    const [imgsrc,setImgsrc]=useState("")
    const {id}=useParams()
    useEffect(() => {
      if (data) {
        setImgsrc(data.image);
      }
    }, [data]);

    const handleChange = (image) => {
      setImgsrc(image);
    };
    console.log(id)
    const { isLoading, isError, data, error }= useQuery(['products'],()=>{
        return axios.get(`https://obscure-refuge-62167.herokuapp.com/products/${id}`);
      })
      if (isLoading) {
        return <span>Loading...</span>
      }

      if (isError) {
        console.log(error)
        return <span>Error: {error.message}</span>
      }
      let t=""
      console.log(data.data)
      console.log(count)
      const {variants } = data;
    return (
        <div className="item">
            <img width={400} height={400} src={imgsrc} alt="product-1" />
            <div  className="description">
                <h4>Name:{data.data.name}</h4>
                <p>Description:{data.data.description}</p>
                <p>Price:{data.data.price}</p>
                <p>Category:{data.data.category}</p>
                <div></div>

                <p>Quantity:{text}</p>
                <button id="btn" onClick={()=>{setCount(count-1);setText(
                  (count > 1 && count<10)?"sellingfast": (count>=10?" available":"unavailable")
                )}}  >-</button>
                {count}
                <button id="btn"  onClick={()=>{setCount(count+1);setText(
                  (count > 0 && count<10)?"sellingfast": (count>=10?" available":"unavailable")
                )}} >+</button>
                <div></div>

                <p>Variants:</p>
                <button id="btnc" style={{backgroundColor:data.data.variants[0].color}}
                onClick={setImgsrc(data.data.variant[0].image)}

                >1</button>

                <button id="btnc" style={{backgroundColor:data.data.variants[1].color}}>2</button>

                <button id="btn" >Add to Cart</button>
            </div>   
        </div>
    );
}
