import {useQuery} from "react-query"
import axios from "axios"
import {NavLink } from "react-router-dom";

export const Home=()=>{
    const { isLoading, isError, data, error }= useQuery(['products'],()=>{
        return axios.get('https://obscure-refuge-62167.herokuapp.com/products');
      })
    if (isLoading) {
        return <span>Loading...</span>
    }

    if (isError) {
        console.log(error)
        return <span>Error: {error.message}</span>
    }
    return (
        <>
        <div className="nav2">
            <li id="prod"><h1>Products</h1></li>
            <li  ><button id="btn" style={{marginTop:"30px",backgroundColor:"blue",color:"white",height:"30px",borderRadius:"4px"}}>Create Product</button></li>
        </div>
        <div className="image">
            {
             data.data.map((i)=>{ return (<>
             <img width={400} src={i.image} alt="product" />

             </>)})
            }
        </div>
        </>
    )
}
