import { useParams } from "react-router-dom";
import {useQuery} from "react-query"
import {useState,useEffect} from "react"
import axios from "axios";
import { atom,useRecoilState } from "recoil";
import {AddCart} from "./AddCart"
import '../App.css';


export const cartState=atom({
  key:'cart',
  default:[],
})

export const Products=()=>{


    const [count,setCount]=useState(0)
    const [text,setText]=useState("unavailable")
    const [cart,setCart]=useRecoilState(cartState)
    const {id}=useParams()
    const [cartvalue,setCartValue]=useState(0);


    //console.log(id)
    const { isLoading, isError, data, error }= useQuery(['product', id],async ()=>{
        return  await axios.get(`https://obscure-refuge-62167.herokuapp.com/products/${id}`);
      })
      const [imgsrc,setImgsrc]=useState('')
    useEffect(()=>
    {
      if (data) 
      {
        setImgsrc(data.data.image);
      }
    }, [data]);


      const handleChange =(img)=>
      {
        setImgsrc(img);
      };
      const handleDisable =(count)=>
      {
       return count===0?"true":"false" 
      };

      if (isLoading) {
        return <span>Loading...</span>
      }

      if (isError) {
        console.log(error)
        return <span>Error: {error.message}</span>
      }
      let t=""
      console.log(data.data.quantity)
      console.log(count)



      const {id:i,name,price,quantity,variants,image} = data.data;
      //setText(quantity>10?"available":quantity>=1?"sellingfast":"unavailable")
      // console.log(variants.image)
      //disabled={dis}
      //setDis(
       // (count ===0) ? "true":"false" 
      //)
      console.log(count)
      const addToCart=()=>{
        setCart(()=>[
          {
            id:i,
            image:image,
            name:name,
            quantity:count+1,
            price:price * (count+1),
          }
        ]);
        console.log(quantity)

      };

      const remFromCart=()=>{
        setCart(()=>[
          {}
        ]);
      }

      const loadCart=()=>{
        return <AddCart id={i}/>
      }
      console.log(cart)
      console.log(cart.length)
     // console.log(count)
     // let cname=cart.length>0?"dshow":"show"
     // cname=cart.length==0?"show":"dshow"
      //const handleCart=()=>{
      //  return <div className="cartDisplay">Hello</div>
      //  
      //}

    return (

      data.data?

        <div className="item">

            <img className="link" width={400} height={400} src={imgsrc} alt="product-1" />
            <div  className="description">
                <h3>{data.data.name}</h3>
                <p>Description: {data.data.description}</p>
                <p>Price:<strong>${data.data.price}</strong></p>

                <p>Variants: </p>
                {
                  variants.map((variant)=>{
                    return <button id="btnd" key={variant.color} style={{backgroundColor:variant.color,width:"40px",height:"40px"}}
                    onClick={() => setImgsrc(variant.image)}></button>
                  })
                }

                <p>Quantity:{text}</p>
                {
                  count>0?
                  <div><button id="btn" disabled={count===0?true:false} onClick={()=>{setCount(count-1);
                    setText(
                      (count >=1 && count<10)?"sellingfast": (count>=10?" available":"unavailable")
                    );setCartValue(count)
                    count>0?addToCart():remFromCart();}}>-</button>
                    {count}
                    <button id="btn" disabled={(count>=data.data.quantity)?true:false}  onClick={()=>{setCount(count+1);setText(
                      (count >=1 && count<10)?"sellingfast": (count>=10?" available":"unavailable")

                    );
                    addToCart()}} >+</button>
                    </div>
                    :
                  <button  id="btn" onClick={()=>{setCount(1);addToCart();setText("sellingfast")}} style={{fontSize:"15px"}}>Add to Cart</button>
                }




            </div>   
            <div className="btncart">
              <button id="btn" style={{fontSize:"15px"}} >Cart({count>0?1:0}){count==0?<AddCart/>:""}
              {
                cart.length>0?

              <div className="card">
                <img className="c-image" src={cart[0].image} alt="product"></img>
                  <div className="c-description">
                    <h3>{cart[0].name}</h3>
                    <p>{cart[0].quantity }</p>
                    <p>Price:<b>$</b>{cart[0].price}</p>
                  </div>
              </div>
              : "No items in cart!"
              }</button>
            </div>
        </div>:"undefined"


    );
}
