import { useParams } from "react-router-dom";
import {useQuery} from "react-query"
import {useState,useEffect} from "react"
import axios from "axios";
import { atom,useRecoilState } from "recoil";
import '../App.css';

const cartState=atom({
  key:'cart',
  default:[],
})

export const Products=()=>{


    const [count,setCount]=useState(0)
    const [text,setText]=useState("")
    const [cart,setCart]=useRecoilState(cartState)
    const {id}=useParams()


    //console.log(id)
    const { isLoading, isError, data, error }= useQuery(['product', id],async ()=>{
        return  await axios.get(`https://obscure-refuge-62167.herokuapp.com/products/${id}`);
      })
      const [imgsrc,setImgsrc]=useState('')
    useEffect(()=>
    {
      if (data) 
      {
        setImgsrc(data.data.image);
      }
    }, [data]);


      const handleChange =(img)=>
      {
        setImgsrc(img);
      };
      const handleDisable =(count)=>
      {
       return count===0?"true":"false" 
      };

      if (isLoading) {
        return <span>Loading...</span>
      }

      if (isError) {
        console.log(error)
        return <span>Error: {error.message}</span>
      }
      let t=""
      console.log(data.data.quantity)
      console.log(count)



      const {id:i,name,price,quantity,variants} = data.data;
      //setText(quantity>10?"available":quantity>=1?"sellingfast":"unavailable")
      // console.log(variants.image)
      //disabled={dis}
      //setDis(
       // (count ===0) ? "true":"false" 
      //)
      console.log(count)
      const addToCart=()=>{
        setCart((oldcart)=>[
          ...oldcart,
          {
            id:i,
            name:name,
            quantity:quantity,
            price:price * cart.length,
          }
        ]);
        setCount(1);
      };
      console.log(cart)
      console.log(cart.length)
     // console.log(count)
     // let cname=cart.length>0?"dshow":"show"
     // cname=cart.length==0?"show":"dshow"


    return (

      data.data?

        <div className="item">

            <img className="link" width={400} height={400} src={imgsrc} alt="product-1" />
            <div  className="description">
                <h4>Name:{data.data.name}</h4>
                <p>Description:{data.data.description}</p>
                <p>Price:{data.data.price}</p>
                <p>Category:{data.data.category}</p>

                <p>Variants: </p>
                <button id="btnc" style={{backgroundColor:variants[0].color}}
                onClick={(e) => {
                  setImgsrc(variants[0].image);
                }}
                >1</button>

                <button id="btnc" style={{backgroundColor:variants[1].color}}
                onClick={(e) => {
                  setImgsrc(variants[1].image);
                }}
                >2</button>




                <p>Quantity:{text}</p>

                  <div >

                  <button id="btn" disabled={count===0?true:false} onClick={()=>{setCount(count-1);
                  setText(
                    (count >=1 && count<10)?"sellingfast": (count>=10?" available":"unavailable")
                  )}}>-</button>
                  {count}
                  <button id="btn" disabled={(count>=data.data.quantity)?true:false}  onClick={()=>{setCount(count+1);setText(
                    (count >=1 && count<10)?"sellingfast": (count>=10?" available":"unavailable")

                  )}} >+</button>

                  </div>

                <button className={(cart.length>0?"dshow":"show")} id="btn" onClick={addToCart} >Add to Cart</button>






            </div>   
            <div className="btncart"><button id="btn">Cart({cart.length})</button>
              </div>
        </div>:"undefined"


    );
}
