import { useParams } from "react-router-dom";
import {useQuery} from "react-query"
import {useState,useEffect} from "react"
import axios from "axios";
import '../App.css';

export const Products=()=>{

    const [count,setCount]=useState(0)
    const [dis,setDis]=useState("false")
    const [text,setText]=useState("")
    const [imgsrc,setImgsrc]=useState("")
    const {id}=useParams()


    console.log(id)
    const { isLoading, isError, data, error }= useQuery(['products'],()=>{
        return axios.get(`https://obscure-refuge-62167.herokuapp.com/products/${id}`);
      })
      useEffect(()=>
      {
        if (data) 
        {
          setImgsrc(data.data.image);
        }
      }, [data]);

      const handleChange =(img)=>
      {
        setImgsrc(img);
      };
      const handleDisable =(count)=>
      {
       return count===0?"true":"false" 
      };

      if (isLoading) {
        return <span>Loading...</span>
      }

      if (isError) {
        console.log(error)
        return <span>Error: {error.message}</span>
      }
      let t=""
      console.log(data.data)

      console.log(count)


      const {quantity,variants} = data.data;
      setText()
      // console.log(variants.image)
      //disabled={dis}
    return (
        <div className="item">
            <img width={400} height={400} src={imgsrc} alt="product-1" />
            <div  className="description">
                <h4>Name:{data.data.name}</h4>
                <p>Description:{data.data.description}</p>
                <p>Price:{data.data.price}</p>
                <p>Category:{data.data.category}</p>
                <p>Quantity:{text}</p>
                <button id="btn" onClick={()=>{setCount(count-1);
                setText(
                  (count >=1 && count<10)?"sellingfast": (count>=10?" available":"unavailable")
                )
                setDis(
                  (count ===0) ? "true":"false" 
                )}}
                >-</button>
                {count}
                <button id="btn"  onClick={()=>{setCount(count+1);setText(
                  (count >=1 && count<10)?"sellingfast": (count>=10?" available":"unavailable")
                )}} >+</button>
                <div></div>




                <p>Variants: </p>
                <button id="btnc" style={{backgroundColor:variants[0].color}}
                onClick={(e) => {
                  setImgsrc(variants[0].image);
                }}
                >1</button>

                <button id="btnc" style={{backgroundColor:variants[1].color}}
                onClick={(e) => {
                  setImgsrc(variants[1].image);
                }}
                >2</button>


                <div></div>
                <button id="btn" >Add to Cart</button>
            </div>   
        </div>
    );
}
