import "./AddCart.css"
import axios from "axios";
import {useQuery} from "react-query";
import {cartState} from "./Products"

export const AddCart=({id,cart})=>{
    const {isLoading,isError,data:product}=useQuery(['prod',id], async () => {
        const { data } = await axios.get(cart.image);
    return data;
    })
    if(isError || isLoading)
    {
        return <h3>Loading...</h3>
    }
    return (
        cart.map((cartitem)=>{
            return(
            <div className="card">
            <img class="c-image" src={product.image} alt="product"></img>
            <div className="c-description">
                <h3>{cartitem.name}</h3>
                <p>{cart.quantity }</p>
                <p>Price:<b>$</b>{cart.price}</p>
            </div>
            </div>
            )
        })

    )
}
