import { useParams } from "react-router-dom";
import {useQuery} from "react-query"
import {useState} from "react"
import axios from "axios";
import '../App.css';

export const Products=()=>{
    const [count,setCount]=useState(1)
    const [text,setText]=useState("sellingfast")
    const {id}=useParams()
    console.log(id)
    const { isLoading, isError, data, error }= useQuery(['products'],()=>{
        return axios.get(`https://obscure-refuge-62167.herokuapp.com/products/${id}`);
      })
      if (isLoading) {
        return <span>Loading...</span>
      }

      if (isError) {
        console.log(error)
        return <span>Error: {error.message}</span>
      }
      console.log(data.data)
      if(data.data.quantity > 10)
      { setText(" available")}
      else if(data.data.quantity == 0)
      { setText(text=" unavailable")}
      else
      {setText(text=" sellingfast")}
    return (
        <div className="item">
            <img width={400} height={400} src={data.data.image} alt="product-1" />
            <div  className="description">
                <h4>Name:{data.data.name}</h4>
                <p>Description:{data.data.description}</p>
                <p>Price:{data.data.price}</p>
                <p>Category:{data.data.category}</p>
                <p>Quantity:{text}</p>
                <button id="btn" onClick={()=>{setCount(count-1);setText(
                  count > 10?" available":" unavailable"
                )}}>-</button>
                {count}
                <button id="btn" onClick={()=>setCount(count+1)}>+</button>
                <div></div>
                <button id="btn" >Add to Cart</button>
            </div>   
        </div>
    );
}
