import { useParams } from "react-router-dom";
import {useQuery} from "react-query"
import {useState} from "react"
import axios from "axios";
import '../App.css';

export const Products=()=>{
    const [count,setCount]=useState(0)
    const [text,setText]=useState("")
    const {id}=useParams()
    console.log(id)
    const { isLoading, isError, data, error }= useQuery(['products'],()=>{
        return axios.get(`https://obscure-refuge-62167.herokuapp.com/products/${id}`);
      })
      if (isLoading) {
        return <span>Loading...</span>
      }

      if (isError) {
        console.log(error)
        return <span>Error: {error.message}</span>
      }
      let t=""
      console.log(data.data)
    return (
        <div className="item">
            <img width={400} height={400} src={data.data.image} alt="product-1" />
            <div  className="description">
                <h4>Name:{data.data.name}</h4>
                <p>Description:{data.data.description}</p>
                <p>Price:{data.data.price}</p>
                <p>Category:{data.data.category}</p>
                <p>Quantity:{text}</p>
                <button id="btn" onClick={()=>{setCount(count-1);setText(
                  (count > 0 && count<10)?"sellingfast": (count>=10?" available":"unavailable")
                )}}  disabled={count=0?"true":"false"}>-</button>
                {count}
                <button id="btn" onClick={()=>{setCount(count+1);setText(
                  (count > 0 && count<10)?"sellingfast": (count>=10?" available":"unavailable")
                )}} disabled={count>=data.data.qunatity?"true":"false"}>+</button>
                <div></div>
                <button id="btn" >Add to Cart</button>
            </div>   
        </div>
    );
}
