import "./AddCart.css"
import axios from "axios";
import {useQuery} from "react-query";
import {cartState} from "./Products"

export const AddCart=({id,count,price})=>{
    const {isLoading,isError,data:product}=useQuery(['prod',id], async () => {
        const { data } = await axios.get(`https://obscure-refuge-62167.herokuapp.com/products/${id}`);
    return data;
    })
    if(isError || isLoading)
    {
        return <h3>Loading...</h3>
    }
    return (
        <div className="card">
            <img class="c-image" src={cartState.image} alt="product"></img>
            <div className="c-description">
                <h3>{cartState.name}</h3>
                <p>{cartState.quantity }</p>
                <p>Price:<b>$</b>{cartState.price}</p>
            </div>
        </div>
    )
}
