import { useParams } from "react-router-dom";

export const Products=()=>{
    const {id}=useParams()
    const {data}=axios.fetch(`https://obscure-refuge-62167.herokuapp.com/products/${id}`)
    return (
        <div className="item">
            <img src={data.image} alt="product-1" />
            <div  className="description">
                <h4>Name:{data.name}</h4>
                <p>Description:{data.description}</p>
                <p>Price:{data.price}</p>
                <p>In Stock</p>
                <button id="btn" style={{marginTop:"30px",backgroundColor:"blue",color:"white",height:"30px",borderRadius:"4px"}}>Add to Cart</button>
            </div>   
        </div>
    );
}
