import { useParams } from "react-router-dom";
import {useQuery} from "react-query"
import {useState,useEffect} from "react"
import axios from "axios";
import { atom,useRecoilState } from "recoil";
import '../App.css';

const cartState=atom({
  key:'cart',
  default:[],
})

export const Products=()=>{


    const [count,setCount]=useState(0)
    const [text,setText]=useState("")
    const [cart,setCart]=useRecoilState(cartState)
    const {id}=useParams()


    //console.log(id)
    const { isLoading, isError, data, error }= useQuery(['product', id],async ()=>{
        return  await axios.get(`https://obscure-refuge-62167.herokuapp.com/products/${id}`);
      })
      const [imgsrc,setImgsrc]=useState('')
    useEffect(()=>
    {
      if (data) 
      {
        setImgsrc(data.data.image);
      }
    }, [data]);


      const handleChange =(img)=>
      {
        setImgsrc(img);
      };
      const handleDisable =(count)=>
      {
       return count===0?"true":"false" 
      };

      if (isLoading) {
        return <span>Loading...</span>
      }

      if (isError) {
        console.log(error)
        return <span>Error: {error.message}</span>
      }
      let t=""
      console.log(data.data.quantity)
      console.log(count)



      const {id:i,name,price,quantity,variants} = data.data;
      //setText(quantity>10?"available":quantity>=1?"sellingfast":"unavailable")
      // console.log(variants.image)
      //disabled={dis}
      //setDis(
       // (count ===0) ? "true":"false" 
      //)
      console.log(count)
      const addToCart=()=>{
        setCart(()=>[
          {
            id:i,
            name:name,
            quantity:count+1,
            price:price * (count+1),
          }
        ]);
        console.log(quantity)

      };
      const remFromCart=()=>{
        setCart(()=>[
          {}
        ]);
      }
      console.log(cart)
      console.log(cart.length)
     // console.log(count)
     // let cname=cart.length>0?"dshow":"show"
     // cname=cart.length==0?"show":"dshow"
      //const handleCart=()=>{
      //  return <div className="cartDisplay">Hello</div>
      //  
      //}

    return (

      data.data?

        <div className="item">

            <img className="link" width={400} height={400} src={imgsrc} alt="product-1" />
            <div  className="description">
                <h3>{data.data.name}</h3>
                <p>{data.data.description}</p>
                <p>Price:<strong>${data.data.price}</strong></p>

                <p>Variants: </p>
                {
                  variants.map
                }
                <button id="btnc" style={{backgroundColor:variants[0].color,width:"40px",height:"40px"}}
                onClick={(e) => {
                  setImgsrc(variants[0].image);
                }}
                >1</button>

                <button id="btnd" style={{backgroundColor:variants[1].color,width:"40px",height:"40px"}}
                onClick={(e) => {
                  setImgsrc(variants[1].image);
                }}
                >2</button>




                <p>Quantity:{text}</p>
                {
                  count>0?
                  <div><button id="btn" disabled={count===0?true:false} onClick={()=>{setCount(count-1);
                    setText(
                      (count >=1 && count<10)?"sellingfast": (count>=10?" available":"unavailable")
                    );
                    count>0?addToCart():remFromCart()}}>-</button>
                    {count}
                    <button id="btn" disabled={(count>=data.data.quantity)?true:false}  onClick={()=>{setCount(count+1);setText(
                      (count >=1 && count<10)?"sellingfast": (count>=10?" available":"unavailable")

                    );
                    addToCart()}} >+</button>
                    </div>
                    :
                  <button  id="btn" onClick={()=>{setCount(1);addToCart()}} style={{fontSize:"15px"}}>Add to Cart</button>
                }




            </div>   
            <div className="btncart">
              <button id="btn" style={{fontSize:"15px"}} >Cart({count>0?1:0})</button>
            </div>
        </div>:"undefined"


    );
}
